mapper.select:
      
		select username from 
		    users
		WHERE  
		uname = #{uname}
		and 
		utel = #{utel}

      
		select * from 
		    users
		WHERE  
		uname = #{uname}
		and 
		utel = #{utel}
		and 
		username = #{username}

mapper.update:

	UPDATE users 
		SET
	password = #{password}
		WHERE
	username = #{username}
