mapper.select:
      
		select username from 
		    users
		WHERE  
		uname = #{uname}
		and 
		utel = #{utel}

mapper.update:
    	
		update users set 
		password = #{password} 
		where 
		uemail = #{uemail}
		and 
		username = #{username}

mapper.select:
     
		select count(*) from 
		users 
		where 
		uemail = #{uemail} 
		and 
		username = #{username}

